import { BASE_URL } from '../config';
import React, { useEffect, useState } from 'react';
import {
  View, Text, Button, StyleSheet, Alert, TextInput, ScrollView
} from 'react-native';
import axios from 'axios';
import { Picker } from '@react-native-picker/picker';
import { getCurrentLocation } from '../utils/getLocation';

export default function RunnerDashboard({ navigation, route }) {
  const { user } = route.params;
  const [trip, setTrip] = useState(null);
  const [managers, setManagers] = useState([]);
  const [selectedManager, setSelectedManager] = useState(null);
  const [distance, setDistance] = useState('');
  const [parking, setParking] = useState('');
  const [summary, setSummary] = useState({});
  const [tpfa, setTpfa] = useState(0);

  useEffect(() => {
    fetchManagers();
    fetchSummary();
    fetchTPFA();
  }, []);

  const fetchManagers = async () => {
    const res = await axios.get(`${BASE_URL}/api/managers`);
    setManagers(res.data);
  };

  const fetchSummary = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/api/trip/summary/${user._id}`);
      setSummary(res.data);
    } catch (err) {
      console.error('Summary error:', err);
    }
  };

  const fetchTPFA = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/api/trip/pending-count/${user._id}`);
      if (Array.isArray(res.data)) {
        const managerCounts = res.data.map(item => `${item.managerName} (${item.count})`).join(', ');
        setTpfa(managerCounts);
      } else {
        setTpfa(res.data.count || 0);
      }
    } catch (err) {
      console.error('TPFA error:', err);
    }
  };

  const getCurrentLocation = async () => {
    try {
      const { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Location permission not granted');
        return null;
      }

      const coords = await getCurrentLocation();
      console.log('üìç Current Location:', location.coords);
      return location.coords;
    } catch (err) {
      console.error('Location fetch error:', err);
      return null;
    }
  };

  const startTrip = async () => {
    if (!selectedManager) {
      Alert.alert('Please select a manager');
      return;
    }

    try {
      const coords = await getCurrentLocation();
      if (!coords) {
        Alert.alert('Could not get location. Please try again.');
        return;
      }

      const res = await axios.post(`${BASE_URL}/api/trip/start`, {
        userId: user._id,
        managerId: selectedManager,
        startLat: coords.latitude,
        startLng: coords.longitude,
      });
      setTrip(res.data);
      fetchTPFA();
    } catch (err) {
      console.error('Trip start failed:', err);
      Alert.alert('Failed to start trip');
    }
  };

  const stopTrip = async () => {
    try {
      const coords = await getCurrentLocation();
      if (!coords) {
        Alert.alert('Could not get location. Please try again.');
        return;
      }

      const res = await axios.post(`${BASE_URL}/api/trip/stop/${trip._id}`, {
        endLat: coords.latitude,
        endLng: coords.longitude
      });
      setTrip(res.data);
      fetchSummary();
      fetchTPFA();
    } catch (err) {
      console.error('Trip stop failed:', err);
      Alert.alert('Failed to stop trip');
    }
  };

  const updateParking = async () => {
    try {
      const res = await axios.post(`${BASE_URL}/api/trip/update-parking/${trip._id}`, {
        parkingAmount: parseFloat(parking)
      });
      setTrip(res.data);
      fetchSummary();
      Alert.alert('Parking Charges Updated');
    } catch (err) {
      console.error('Parking update failed:', err);
      Alert.alert('Failed to update parking');
    }
  };

  const logout = () => navigation.navigate('Login');
  const resetTrip = () => {
    setTrip(null);
    setDistance('');
    setParking('');
    fetchSummary();
    fetchTPFA();
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}>Runner Dashboard</Text>
      <Text style={styles.header}>Welcome {user.name}</Text>
      <View style={styles.tpfaBox}><Text style={styles.tpfaText}>TPFA: {tpfa}</Text></View>

      {!trip && (
        <>
          <View style={styles.pickerContainer}>
            <Picker
              selectedValue={selectedManager}
              onValueChange={(val) => setSelectedManager(val)}
              style={styles.picker}
              dropdownIconColor="#003366"
            >
              <Picker.Item label="Select Manager" value={null} />
              {managers.map(manager => (
                <Picker.Item key={manager._id} label={manager.name} value={manager._id} />
              ))}
            </Picker>
          </View>
          <Button title="Start Trip" onPress={startTrip} color="#003366" />
        </>
      )}

      {trip && (
        <View style={styles.card}>
          <Text style={styles.status}>Trip Status: {trip.endTime ? 'Completed' : 'Started'}</Text>
          <Text>Trip ID: {trip._id}</Text>
          <Text>Manager: {trip.managerName || 'N/A'}</Text>
          <Text>Started at: {trip.startTime ? new Date(trip.startTime).toLocaleString() : ''}</Text>
          <Text>Start Location: Lat {trip.startLoc?.lat}, Lng {trip.startLoc?.lng}</Text>
          <Text>Ended at: {trip.endTime ? new Date(trip.endTime).toLocaleString() : ''}</Text>
          <Text>End Location: Lat {trip.endLoc?.lat}, Lng {trip.endLoc?.lng}</Text>
          <Text>Distance: {trip.distance || 0} km</Text>
          <Text>Parking Charges: ‚Çπ{trip.parkingAmount || 0}</Text>
          <Text>Total Payment: ‚Çπ{trip.payment || 0}</Text>

          {!trip.endTime && (
            <>
              <Button title="Stop Trip" onPress={stopTrip} color="#28a745" />
            </>
          )}

          {trip.endTime && (
            <>
              <TextInput
                style={styles.input}
                placeholder="Add Parking Charges"
                keyboardType="numeric"
                value={parking}
                onChangeText={setParking}
              />
              <Button title="Update Parking Charges" onPress={updateParking} color="#ff6600" />
              <Button title="Start New Trip" onPress={resetTrip} color="#17a2b8" />
            </>
          )}
        </View>
      )}

      <View style={styles.summary}>
        <Text style={styles.subheading}>Weekly Summary:</Text>
        <Text>Total Trips: {summary.totalTrips}</Text>
        <Text>Total Distance: {summary.totalDistance} km</Text>
        <Text>Total Parking: ‚Çπ{summary.totalParking}</Text>
        <Text>Total Payment: ‚Çπ{summary.totalPayment}</Text>
      </View>

      <Button title="View Report" onPress={() => navigation.navigate('RunnerReport', { user })} color="#003366" />
      <Button title="Log Out" onPress={logout} color="#dc3545" />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { padding: 20, backgroundColor: '#f2f2f2', flexGrow: 1 },
  title: { fontSize: 22, fontWeight: 'bold', marginBottom: 10, textAlign: 'center', color: '#003366' },
  header: { fontSize: 18, marginBottom: 10, textAlign: 'center', color: '#003366' },
  pickerContainer: {
    backgroundColor: '#fff',
    borderColor: '#003366',
    borderWidth: 1.2,
    borderRadius: 8,
    marginBottom: 10,
    overflow: 'hidden',
  },
  picker: { height: 48, color: '#003366', fontSize: 16, paddingHorizontal: 8 },
  card: { padding: 15, backgroundColor: '#fff', marginBottom: 20, borderRadius: 10 },
  input: { borderColor: '#ccc', borderWidth: 1, borderRadius: 5, padding: 10, marginTop: 10, marginBottom: 10 },
  status: { fontWeight: 'bold', marginBottom: 5, color: '#003366' },
