// === WeeklySummaryScreen.js ===
import { BASE_URL } from '../config';
import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ActivityIndicator, Alert } from 'react-native';
import axios from 'axios';

export default function WeeklySummaryScreen({ route }) {
  const { userId } = route.params;
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchSummary();
  }, []);

  const fetchSummary = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/api/trip/summary/${userId}`);
      setSummary(res.data);
    } catch (err) {
      console.error('Summary fetch failed:', err);
      Alert.alert('Failed to fetch summary.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <ActivityIndicator style={{ marginTop: 30 }} />;

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Weekly Summary</Text>
      <Text style={styles.item}>Total Trips: {summary.totalTrips}</Text>
      <Text style={styles.item}>Total Distance: {summary.totalDistance} km</Text>
      <Text style={styles.item}>Total Parking: ₹{summary.totalParking}</Text>
      <Text style={styles.item}>Total Payment: ₹{summary.totalPayment}</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 30,
    backgroundColor: '#f0f4f8',
    flex: 1,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#007bff',
    marginBottom: 20,
    textAlign: 'center',
  },
  item: {
    fontSize: 18,
    marginBottom: 10,
  },
});
