import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Image,
  StyleSheet,
  Alert,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { Picker } from '@react-native-picker/picker';
import { BASE_URL } from '../config';

export default function LoginScreen({ navigation }) {
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('runner');

  const handleLogin = async () => {
    if (!phone || !password) {
      Alert.alert('Error', 'Please enter both phone and password');
      return;
    }

    try {
      const res = await axios.post(`${BASE_URL}/api/login`, { phone, password });
      const user = res.data;

      if (user.role !== role) {
        Alert.alert('Error', `This user is not a ${role}`);
        return;
      }

      await AsyncStorage.setItem('user', JSON.stringify(user));

      if (role === 'runner') {
        navigation.replace('RunnerDashboard', { user });
      } else if (role === 'manager') {
        navigation.replace('ManagerDashboard', { user });
      } else {
        navigation.replace('AdminDashboard', { user });
      }
    } catch (err) {
      console.error('Login error:', err);
      Alert.alert('Login Failed', 'Invalid credentials');
    }
  };

  return (
    <KeyboardAvoidingView
      behavior={Platform.OS === 'ios' ? 'padding' : undefined}
      style={styles.outerContainer}
    >
      <View style={styles.card}>
        <Image source={require('../assets/Logo.png')} style={styles.logo} />
        <Text style={styles.title}>DBX Runner</Text>

        <View style={styles.pickerWrapper}>
          <Picker
            selectedValue={role}
            onValueChange={(val) => setRole(val)}
            style={styles.picker}
            dropdownIconColor="#003366"
          >
            <Picker.Item label="Runner" value="runner" />
            <Picker.Item label="Manager" value="manager" />
            <Picker.Item label="Admin" value="admin" />
          </Picker>
        </View>

        <TextInput
          style={styles.input}
          placeholder="Phone"
          value={phone}
          onChangeText={setPhone}
          keyboardType="phone-pad"
        />
        <TextInput
          style={styles.input}
          placeholder="Password"
          value={password}
          onChangeText={setPassword}
          secureTextEntry
        />

        <TouchableOpacity style={styles.loginButton} onPress={handleLogin}>
          <Text style={styles.loginButtonText}>Login</Text>
        </TouchableOpacity>
      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  outerContainer: {
    flex: 1,
    backgroundColor: '#002B5B', // Logo Blue
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    backgroundColor: '#ffffff',
    padding: 24,
    borderRadius: 12,
    width: '30%',
    elevation: 5,
    alignItems: 'center',
  },
  logo: {
    width: 240,
    height: 240,
    resizeMode: 'contain',
    marginBottom: 20,
  },
  title: {
    fontSize: 26,
    fontWeight: 'bold',
    color: '#003366',
    marginBottom: 20,
  },
  pickerWrapper: {
    width: '100%',
    backgroundColor: '#f1f1f1',
    borderRadius: 10,
    marginBottom: 14,
    overflow: 'hidden',
  },
  picker: {
    height: 48,
    width: '100%',
    color: '#003366',
  },
  input: {
    width: '100%',
    backgroundColor: '#f9f9f9',
    padding: 12,
    borderRadius: 6,
    marginBottom: 12,
    fontSize: 16,
  },
  loginButton: {
    backgroundColor: '#FFA500',
    padding: 14,
    borderRadius: 6,
    width: '100%',
    alignItems: 'center',
    marginTop: 10,
  },
  loginButtonText: {
    fontWeight: 'bold',
    fontSize: 16,
    color: '#003366',
  },
});
