// ManagerDashboard.js
import logo from '../assets/Logo.png';
import { Image } from 'react-native';
import { BASE_URL } from '../config';
import React, { useEffect, useRef, useState } from 'react';
import {
  View, Text, StyleSheet, ScrollView, Button, FlatList, TextInput, Alert
} from 'react-native';
import axios from 'axios';
import { Picker } from '@react-native-picker/picker';
import * as Print from 'expo-print';

export default function ManagerDashboard({ route }) {
  const { user } = route.params;
  const [trips, setTrips] = useState([]);
  const [runners, setRunners] = useState([]);
  const [runnerMap, setRunnerMap] = useState({});
  const [selectedRunner, setSelectedRunner] = useState('all');
  const [filter, setFilter] = useState('month');
  const [statusFilter, setStatusFilter] = useState('all');
  const [kmEdits, setKmEdits] = useState({});
  const reportRef = useRef();

  useEffect(() => {
    fetchRunnerNames();
  }, []);

  useEffect(() => {
    fetchTrips();
  }, [selectedRunner, filter, statusFilter]);

  const fetchRunnerNames = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/api/runners`);
      const map = {};
      res.data.forEach(r => map[r._id] = r.name);
      setRunnerMap(map);
      setRunners(res.data);
    } catch (err) {
      console.error('Runner fetch error:', err);
    }
  };

  const fetchTrips = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/api/trip/all/${user._id}`);
      let filtered = res.data;
      const now = new Date();

      if (selectedRunner !== 'all') {
        filtered = filtered.filter(t => t.runnerId === selectedRunner);
      }

      if (statusFilter !== 'all') {
        filtered = filtered.filter(t => t.status === statusFilter);
      }

      if (filter === 'today') {
        filtered = filtered.filter(t => new Date(t.startTime).toDateString() === now.toDateString());
      } else if (filter === 'week') {
        const weekAgo = new Date();
        weekAgo.setDate(now.getDate() - 7);
        filtered = filtered.filter(t => new Date(t.startTime) >= weekAgo);
      } else if (filter === 'month') {
        filtered = filtered.filter(t => {
          const d = new Date(t.startTime);
          return d.getMonth() === now.getMonth() && d.getFullYear() === now.getFullYear();
        });
      }

      setTrips(filtered);
    } catch (err) {
      console.error('Trip fetch error:', err);
    }
  };

  const handleApprove = async (tripId) => {
    try {
      const editedKM = kmEdits[tripId];
      await axios.post(`${BASE_URL}/api/trip/approve/${tripId}`, {
        adjustedDistance: editedKM || '',
      });
      fetchTrips();
    } catch (err) {
      console.error('Approve error:', err);
    }
  };

  const handleDecline = async (tripId) => {
    try {
      await axios.post(`${BASE_URL}/api/trip/decline/${tripId}`);
      fetchTrips();
    } catch (err) {
      console.error('Decline error:', err);
    }
  };

  const calculateTotals = () => {
  const validTrips = trips.filter(t => t.status !== 'declined');
  const totalKM = validTrips.reduce((sum, t) => (
    sum + (parseFloat(kmEdits[t._id]) || t.adjustedDistance || t.distance || 0)
  ), 0);
  const totalParking = validTrips.reduce((sum, t) => sum + (t.parkingAmount || 0), 0);
  const totalPayment = validTrips.reduce((sum, t) => {
    const dist = parseFloat(kmEdits[t._id]) || t.adjustedDistance || t.distance || 0;
    return sum + (t.payment || dist * 2.8);
  }, 0);
  return { totalKM, totalParking, totalPayment };
};


  const handlePDFExport = async () => {
    try {
      let html = `
      <html>
        <head>
          <style>
            body { font-family: Arial; padding: 20px; }
            h2 { text-align: center; color: #003366; }
            table { width: 100%; border-collapse: collapse; margin-top: 20px; }
            th, td { border: 1px solid #ccc; padding: 8px; text-align: center; font-size: 12px; }
            th { background-color: #003366; color: white; }
            tfoot td { font-weight: bold; background-color: #f2f2f2; }
          </style>
        </head>
        <body>
          <div style="text-align:center">
  <img src="data:image/png;base64,YOUR_BASE64_STRING_HERE" style="height:60px;margin-bottom:10px;" />
  <h2>Manager Trip Report – ${user.name}</h2>
</div>
          <table>
            <thead>
              <tr>
                <th>Trip ID</th><th>Runner</th><th>Start</th><th>End</th><th>KM</th><th>₹Park</th><th>₹Pay</th><th>From</th><th>To</th><th>Status</th>
              </tr>
            </thead>
            <tbody>
              ${trips.map(t => `
                <tr>
                  <td>${t._id.slice(-5)}</td>
                  <td>${runnerMap[t.runnerId] || ''}</td>
                  <td>${t.startTime ? new Date(t.startTime).toLocaleString() : '-'}</td>
                  <td>${t.endTime ? new Date(t.endTime).toLocaleString() : '-'}</td>
                  <td>${parseFloat(kmEdits[t._id]) || t.adjustedDistance || t.distance || 0}</td>
                  <td>${t.parkingAmount || 0}</td>
                  <td>${t.payment || 0}</td>
                  <td>${t.startLoc ? `${t.startLoc.lat.toFixed(2)},${t.startLoc.lng.toFixed(2)}` : '-'}</td>
                  <td>${t.endLoc ? `${t.endLoc.lat.toFixed(2)},${t.endLoc.lng.toFixed(2)}` : '-'}</td>
                  <td>${t.status}</td>
                </tr>
              `).join('')}
            </tbody>
            <tfoot>
              <tr>
                <td colspan="4">Totals</td>
                <td>${calculateTotals().totalKM.toFixed(2)}</td>
                <td>${calculateTotals().totalParking.toFixed(2)}</td>
                <td>${calculateTotals().totalPayment.toFixed(2)}</td>
                <td colspan="3"></td>
              </tr>
            </tfoot>
          </table>
        </body>
      </html>`;

      await Print.printAsync({ html });
    } catch (err) {
      console.error('PDF export error:', err);
      Alert.alert('Print error');
    }
  };

  const totals = calculateTotals();

  return (
    <ScrollView style={{ padding: 10 }}>
      <View style={styles.headerRow}>
  <Text style={styles.title}>Manager Dashboard</Text>
  <Image source={logo} style={styles.logo} resizeMode="contain" />
</View>

<Text style={styles.managerName}>Welcome, {user.name}</Text>

      <View style={styles.filterRow}>
        <Picker selectedValue={selectedRunner} style={styles.picker} onValueChange={setSelectedRunner}>
          <Picker.Item label="All Runners" value="all" />
          {runners.map(r => <Picker.Item key={r._id} label={r.name} value={r._id} />)}
        </Picker>

        <Picker selectedValue={filter} style={styles.picker} onValueChange={setFilter}>
          <Picker.Item label="Today" value="today" />
          <Picker.Item label="This Week" value="week" />
          <Picker.Item label="This Month" value="month" />
        </Picker>

        <Picker selectedValue={statusFilter} style={styles.picker} onValueChange={setStatusFilter}>
          <Picker.Item label="All Statuses" value="all" />
          <Picker.Item label="Pending" value="Pending" />
          <Picker.Item label="Approved" value="approved" />
          <Picker.Item label="Declined" value="declined" />
        </Picker>
      </View>

      <View style={styles.rowHeader}>
        {["Trip ID", "Runner", "Start", "End", "KM", "₹Park", "₹Pay", "From", "To", "Status", "Actions"].map((h, i) => (
          <Text key={i} style={styles.cellHeader}>{h}</Text>
        ))}
      </View>

      {trips.map((item) => (
        <View key={item._id} style={styles.row}>
          <Text style={styles.cell}>{item._id.slice(-5)}</Text>
          <Text style={styles.cell}>{runnerMap[item.runnerId]}</Text>
          <Text style={styles.cell}>{new Date(item.startTime).toLocaleTimeString()}</Text>
          <Text style={styles.cell}>{item.endTime ? new Date(item.endTime).toLocaleTimeString() : '-'}</Text>
          <TextInput
            style={styles.input}
            keyboardType="numeric"
            placeholder={`${item.adjustedDistance || item.distance || 0}`}
            value={kmEdits[item._id]}
            onChangeText={val => setKmEdits(prev => ({ ...prev, [item._id]: val }))}
          />
          <Text style={styles.cell}>₹{item.parkingAmount || 0}</Text>
          <Text style={styles.cell}>₹{item.payment || 0}</Text>
          <Text style={styles.cell}>{item.startLoc ? `${item.startLoc.lat.toFixed(2)},${item.startLoc.lng.toFixed(2)}` : '-'}</Text>
          <Text style={styles.cell}>{item.endLoc ? `${item.endLoc.lat.toFixed(2)},${item.endLoc.lng.toFixed(2)}` : '-'}</Text>
          <Text style={styles.cell}>{item.status}</Text>
          <View style={styles.actionCell}>
            <Button title="✔" onPress={() => handleApprove(item._id)} color="#28a745" />
            <Button title="✖" onPress={() => handleDecline(item._id)} color="#dc3545" />
          </View>
        </View>
      ))}

      <View style={styles.totalBlock}>
        <Text style={styles.totalText}>Total KM: {totals.totalKM.toFixed(2)}</Text>
        <Text style={styles.totalText}>Total Parking: ₹{totals.totalParking.toFixed(2)}</Text>
        <Text style={styles.totalText}>Total Payment: ₹{totals.totalPayment.toFixed(2)}</Text>
      </View>

      <Button title="Download PDF Report" onPress={handlePDFExport} color="#ff6600" />
    </ScrollView>
  );
}

